import React, { useState } from 'react';

function Login() {
  const [correo, setCorreo] = useState('');
  const [contrasenia, setContrasenia] = useState('');
  const [mensaje, setMensaje] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    const data = new FormData();
    data.append('Correo', correo);
    data.append('Contrasenia', contrasenia);

    try {
      const response = await fetch('http://localhost:5000/login', {
        method: 'POST',
        body: data,
      });

      const responseData = await response.json();

      if (response.ok && responseData.success) {
        // Inicio de sesión exitoso
        alert(responseData.message); // Muestra una alerta
        // Redirige al usuario a la página de bienvenida con el nombre de usuario
        window.location.href = `/bienvenida/${responseData.nombre_usuario}`;
      } else {
        // Muestra un mensaje de error en el formulario
        setMensaje(responseData.message || 'Error desconocido');
      }
    } catch (error) {
      console.error('Error al procesar la solicitud:', error);
      setMensaje('Error de red. Inténtalo de nuevo.');
    }
  };

  return (
    <div>
      <h2>Iniciar Sesión</h2>
      <form onSubmit={handleSubmit}>
        {/* ... Campos de correo y contraseña ... */}
        <button type="submit">Iniciar Sesión</button>
      </form>
      {mensaje && <p>{mensaje}</p>}
    </div>
  );
}

export default Login;
