import React, { useState } from 'react';

function Login() {
  const [correo, setCorreo] = useState('');
  const [contrasenia, setContrasenia] = useState('');
  const [mensaje, setMensaje] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    const data = new FormData();
    data.append('Correo', correo);
    data.append('Contrasenia', contrasenia);

    try {
      const response = await fetch('http://localhost:5000/login', {
        method: 'POST',
        body: data,
      });

      const responseData = await response.json();

      if (response.ok) {
        // Inicio de sesión exitoso, redirige al usuario a la página de inicio
        // Puedes usar react-router para la navegación
        window.location.href = '/Bienvenida'; // Reemplaza con tu URL de inicio
      } else {
        // Muestra el mensaje de error en el formulario
        setMensaje(responseData.error || 'Error desconocido');
      }
    } catch (error) {
      console.error('Error al procesar la solicitud:', error);
      setMensaje('Error de red. Inténtalo de nuevo.');
    }
  };

  return (
    <div>
      <h2>Iniciar Sesión</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          placeholder="Correo"
          value={correo}
          onChange={(e) => setCorreo(e.target.value)}
        />
        <input
          type="password"
          placeholder="Contraseña"
          value={contrasenia}
          onChange={(e) => setContrasenia(e.target.value)}
        />
        <button type="submit">Iniciar Sesión</button>
      </form>
      {mensaje && <p>{mensaje}</p>}
    </div>
  );
}

export default Login;
